// src/components/Canvas.jsx
import React, { useCallback, useState } from 'react';
import ReactFlow, {
  addEdge,
  Background,
  MiniMap,
  Controls,
  ReactFlowProvider,
  useNodesState,
  useEdgesState
} from 'react-flow-renderer';
import CustomNode from './CustomNode';
import ConfigPanel from './ConfigPanel';

const nodeTypes = {
  custom: CustomNode
};

let id = 0;
const getId = () => `node_${id++}`;

const Canvas = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [selectedNode, setSelectedNode] = useState(null);

  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    []
  );

//  OnDrop Checkpoint #1 ///////////////////////////////////////////////////////////////////////

  // const onDrop = useCallback(
  //   (event) => {
  //     event.preventDefault();
  //     const type = event.dataTransfer.getData('application/reactflow');
  //     const position = { x: event.clientX - 250, y: event.clientY };

  //     const nodeId = getId();

  //     const newNode = {
  //       id: nodeId,
  //       type: 'custom',
  //       position,
  //       data: {
  //         label: type,
  //         config: {},
  //         onConfigure: () => {
  //           const current = nodes.find((n) => n.id === nodeId);
  //           setSelectedNode(current);
  //         }
  //       }
  //     };

  //     setNodes((nds) => nds.concat(newNode));
  //   },
  //   [setNodes, nodes]
  // );




    //  OnDrop Checkpoint 2  /////////////////////////////////////////////////////////////////////

    const onDrop = useCallback(
      (event) => {
        event.preventDefault();
        const type = event.dataTransfer.getData('application/reactflow');
        const position = { x: event.clientX - 250, y: event.clientY };
        const nodeId = getId();

        const newNode = {
          id: nodeId,
          type: 'custom',
          position,
          data: {
            label: type,
            config: {},
            onConfigure: () => {
              setSelectedNode((prev) => {
                console.log("Currently selected node : ",selectedNode);
                const current = nodes.find((n) => n.id === nodeId);
                return current;
              });
            }
          }
        };

        setNodes((nds) => nds.concat(newNode));
      },
      [setNodes, nodes]
    );



  const onDragOver = useCallback((event) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = 'move';
  }, []);

  const updateNodeData = (updatedNode) => {
    setNodes((nds) =>
      nds.map((node) => (node.id === updatedNode.id ? updatedNode : node))
    );
    setSelectedNode(updatedNode);
  };

  return (
    <div style={{ display: 'flex', width: '100%' }}>
      <div style={{ flex: 1, height: '100vh' }} onDrop={onDrop} onDragOver={onDragOver}>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          nodeTypes={nodeTypes}
          fitView
        >
          <MiniMap />
          <Controls />
          <Background />
        </ReactFlow>

      </div>
      <ConfigPanel nodeData={selectedNode} updateNodeData={updateNodeData} />
    </div>
  );
};

export default () => (
  <ReactFlowProvider>
    <Canvas />
  </ReactFlowProvider>
);
