// src/components/Canvas.jsx
import React, { useCallback, useState } from 'react';
import ReactFlow, {
  addEdge,
  Background,
  MiniMap,
  Controls,
  ReactFlowProvider,
  useNodesState,
  useEdgesState
} from 'react-flow-renderer';
import CustomNode from './CustomNode';
import ConfigPanel from './ConfigPanel';

const nodeTypes = {
  custom: CustomNode
};

let id = 0;
const getId = () => `node_${id++}`;

const Canvas = () => {
  const [nodes, setNodes, onNodesChange] = useNodesState([]);
  const [edges, setEdges, onEdgesChange] = useEdgesState([]);
  const [selectedNode, setSelectedNode] = useState(null);
  const [workflowStructure, setWorkflowStructure] = useState([]);
  const onConnect = useCallback(
    (params) => setEdges((eds) => addEdge(params, eds)),
    []
  );

//  OnDrop Checkpoint #1 ///////////////////////////////////////////////////////////////////////

  // const onDrop = useCallback(
  //   (event) => {
  //     event.preventDefault();
  //     const type = event.dataTransfer.getData('application/reactflow');
  //     const position = { x: event.clientX - 250, y: event.clientY };

  //     const nodeId = getId();

  //     const newNode = {
  //       id: nodeId,
  //       type: 'custom',
  //       position,
  //       data: {
  //         label: type,
  //         config: {},
  //         onConfigure: () => {
  //           const current = nodes.find((n) => n.id === nodeId);
  //           setSelectedNode(current);
  //         }
  //       }
  //     };

  //     setNodes((nds) => nds.concat(newNode));
  //   },
  //   [setNodes, nodes]
  // );




    //  OnDrop Checkpoint 2  /////////////////////////////////////////////////////////////////////

    // const onDrop = useCallback(
    //   (event) => {
    //     event.preventDefault();
    //     const type = event.dataTransfer.getData('application/reactflow');
    //     const position = { x: event.clientX - 250, y: event.clientY };
    //     const nodeId = getId();

    //     const newNode = {
    //       id: nodeId,
    //       type: 'custom',
    //       position,
    //       data: {
    //         label: type,
    //         config: {},
    //         onConfigure: () => {
    //           setSelectedNode((prev) => {
    //             console.log("Currently selected node : ",selectedNode);
    //             const current = nodes.find((n) => n.id === nodeId);
    //             console.log("current node is : ",current)
    //             return current;
    //           });
    //         }
    //       }
    //     };

    //     setNodes((nds) => nds.concat(newNode));
    //   },
    //   [setNodes, nodes]
    // );



    // onDrop Checkpoint 3 

    const onDrop = useCallback(
      (event) => {
        event.preventDefault();
        const type = event.dataTransfer.getData('application/reactflow');
        const position = { x: event.clientX - 250, y: event.clientY };
        const nodeId = getId();

        const newNode = {
          id: nodeId,
          type: 'custom',
          position,
          data: {
            label: type,
            config: {},
            onConfigure: () => {
              setSelectedNode(newNode); // ðŸ’¡ directly use newNode (no need to find again)
            }
          }
        };

        console.log("current selected node is :",selectedNode)
        setNodes((nds) => [...nds, newNode]);
      },
      [setNodes]
    );


  const onDragOver = useCallback((event) => {
    event.preventDefault();
    event.dataTransfer.dropEffect = 'move';
  }, []);

  const updateNodeData = (updatedNode) => {
    setNodes((nds) =>
      nds.map((node) => (node.id === updatedNode.id ? updatedNode : node))
    );
    setSelectedNode(updatedNode);
  };

  const computeWorkflowStructure = () => {


    console.log("selected node is : ",node)
    const structure = nodes.map((node) => ({
      id: node.id,
      type: node.data.label,
      config: node.data.config || {},
      connections: edges
        .filter((e) => e.source === node.id)
        .map((e) => e.target)
    }));
    setWorkflowStructure(structure);
    console.log('Workflow structure:', structure);
  };

  return (
    <div style={{ display: 'flex', width: '100%' }}>
      <div style={{ flex: 1, height: '100vh' }} onDrop={onDrop} onDragOver={onDragOver}>
        <ReactFlow
          nodes={nodes}
          edges={edges}
          onNodesChange={onNodesChange}
          onEdgesChange={onEdgesChange}
          onConnect={onConnect}
          nodeTypes={nodeTypes}
          fitView
        >
          <button
              onClick={computeWorkflowStructure}
              style={{
                position: 'absolute',
                top: 20,
                left: 20,
                zIndex: 10,
                padding: '6px 12px',
                background: '#007bff',
                color: '#fff',
                border: 'none',
                borderRadius: '5px'
              }}
            >
              Save Workflow
          </button>
          <MiniMap />
          <Controls />
          <Background />
        </ReactFlow>


      </div>

      {selectedNode && (
           <ConfigPanel nodeData={selectedNode} updateNodeData={updateNodeData} />
      )}
    </div>
  );
};

export default () => (
  <ReactFlowProvider>
    <Canvas />
  </ReactFlowProvider>
);
