// src/components/ConfigPanel.jsx
import React, { useState } from 'react';

const ConfigPanel = ({ nodeData, updateNodeData }) => {
  const [inputText, setInputText] = useState('');

  if (!nodeData) return null;

  console.log("Rendering ConfigPanel for:", nodeData.data);
  console.log("config data : ",nodeData.data.config)
  const handleChange = (e) => {

    const { name, value } = e.target;

    console.log("value : ",e.target.value)
    updateNodeData({
      ...nodeData,
      config: {
        ...nodeData.data.config,
        [name]: value
      }
    });
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      e.preventDefault(); // prevent form reload
      console.log("Saved Input:", inputText); // <- you can save it anywhere
      // Optionally clear it
      // setInputText('');
    }
  };

  return (
    <div style={{
      width: '300px',
      background: '#f0f0f0',
      padding: '16px',
      borderLeft: '1px solid #ccc'
    }}>
      <h3>Configure: {nodeData.data.label}</h3>

      {nodeData.data.label === 'LLMEngine' && (
        <>
          <label>Custom Prompt:</label>
          <textarea
            name="customPrompt"
            rows="4"
            value={nodeData.config?.customPrompt || ''}
            onChange={handleChange}
            style={{ width: '100%', marginTop: '8px' }}
          />
        </>
      )}

      {nodeData.data.label === 'KnowledgeBase' && (
        <>
          <label>Upload File (Coming Later)</label>
          <input type="file" disabled style={{ width: '100%' }} />
        </>
      )}

      {nodeData.data.label === "UserQuery" && (
        <>
          <label>Placeholder:</label>
          <input
            type="text"
            name="placeholder"
            value={nodeData.data.config}
            onChange={(e) => setInputText(e.target.value)}
            onKeyDown={handleKeyDown}
            style={{ width: '100%' }}
          />
        </>
      )}
    </div>
  );
};

export default ConfigPanel;
