// src/components/ConfigPanel.jsx
import React from 'react';

const ConfigPanel = ({ nodeData, updateNodeData }) => {
  if (!nodeData) return null;

  const handleChange = (e) => {
    updateNodeData({
      ...nodeData,
      config: {
        ...nodeData.config,
        [e.target.name]: e.target.value
      }
    });
  };

  return (
    <div style={{
      width: '300px',
      background: '#f0f0f0',
      padding: '16px',
      borderLeft: '1px solid #ccc'
    }}>
      <h3>Configure: {nodeData.label}</h3>

      {nodeData.label === 'LLMEngine' && (
        <>
          <label>Custom Prompt:</label>
          <textarea
            name="customPrompt"
            rows="4"
            value={nodeData.config?.customPrompt || ''}
            onChange={handleChange}
            style={{ width: '100%', marginTop: '8px' }}
          />
        </>
      )}

      {nodeData.label === 'KnowledgeBase' && (
        <>
          <label>Upload File (Coming Later)</label>
          <input type="file" disabled style={{ width: '100%' }} />
        </>
      )}

      {nodeData.label === 'UserQuery' && (
        <>
          <label>Placeholder:</label>
          <input
            type="text"
            name="placeholder"
            value={nodeData.config?.placeholder || ''}
            onChange={handleChange}
            style={{ width: '100%' }}
          />
        </>
      )}
    </div>
  );
};

export default ConfigPanel;
